(5, 'Restrictive Industry As Per EPP - 2015', '<p><a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/NEGATIVE_LIST.pdf\" target=\"_blank\">RESTRICTIVE INDUSTRY AS PER EPP - 2015 - DOWNLOAD</a></p>\r\n', 3, '', '', NULL, b'1', 5, '2020-02-22 13:23:00', '2020-05-12 12:39:58'),
(6, 'Interest Subsidy As Per EPP - 2015', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/interest_subsidy_2015%20%281%29.PDF\" target=\"_blank\">INTEREST SUBSIDY AS PER EPP - 2015 - DOWNLOAD</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:15px; width:20px\">\r\n			<p><strong>1</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:135px\">\r\n			<p><strong>Name of the scheme</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:470px\">\r\n			<p><strong>Interest Subsidy Scheme</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:157px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:157px; width:135px\">\r\n			<p>Important Instructions</p>\r\n			</td>\r\n			<td style=\"height:157px; width:470px\">\r\n			<ol>\r\n				<li>All Micro, Small &amp; Medium Enterprises mentioned under clause 1(A) &amp; (B) of the notification of the scheme and set up in the identified blocks of the State of Haryana, which have filed EM Part-II/ Udyog Aadhaar Memorandum with respective District Industries Centre and availed term loan from Financial Institutions (SIDBI, State Financial Institutions, Co-operative Banks, Sarv Haryana Gramin Bank)/ Banks shall be eligible.</li>\r\n				<li>The items of manufacture should not fall in the restrictive list as notified by State Government from time to time.</li>\r\n				<li>CLU/NOC is required from competent authority, if applicable.</li>\r\n				<li>The unit should be in regular production at the time of disbursement of assistance.</li>\r\n				<li>In case of rejection of case, an appeal shall lie with the Director of Industries and Commerce/Administrative Secretary, Industries and Commerce, Haryana within a period of 30 days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:169px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:169px; width:135px\">\r\n			<p>Documents to be enclosed with the request</p>\r\n			</td>\r\n			<td style=\"height:169px; width:470px\">\r\n			<ol>\r\n				<li>Acknowledgement of EM part-II/ UAM registration</li>\r\n				<li>Certificate of Incorporation/ Partnership deed.</li>\r\n				<li>Board resolution/ Power of attorney</li>\r\n				<li>Change of Land Use (CLU)/ NOC from competent authority, if applicable.</li>\r\n				<li>Certificate for Financial Institution on Letter Head of the Bank(Annexure-IV)</li>\r\n				<li>CA Certificate regarding investment in plant &amp; machinery for new unit/expansion/ diversification (Annexure-V)</li>\r\n				<li>Audited balance sheet of the unit of last year (if Applicable).</li>\r\n				<li>Certificate from concerned Deputy Excise &amp; Taxation Commissioner (Sale Tax) regarding net VAT/SGST deposited by the applicant during the financial year of which claim has been filed (Annexure-VI).</li>\r\n				<li>Copy of first sale bill.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:164px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:164px; width:135px\">\r\n			<p>Timeline and Procedure</p>\r\n			</td>\r\n			<td style=\"height:164px; width:470px\">\r\n			<ol>\r\n				<li>Submit application online with all the required documents within three months of the closing of the financial year of which incentive is being claimed.</li>\r\n				<li>Online application submitted shall be processed and examined by concerned Joint Director/ Deputy Director.</li>\r\n				<li>If any discrepancy is found, the application shall be put onhold and be communicated to the applicant within 10 working days.</li>\r\n				<li>The applicant will rectify the deficiencies within 06 weeks failing which the application will liable to be rejected.</li>\r\n				<li>After completion of the application, the approval letter will be issued within 60 working days.</li>\r\n				<li>The applicant will submit the documents mentioned in approval letter within 15 days.</li>\r\n				<li>After compliance of conditions of approval letter, the sanction letter for releasing the incentive will be issued within 15 working days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 15, '', '', NULL, b'1', 6, '2020-02-22 13:23:00', '2020-05-12 12:43:05'),
(7, 'Stamp Duty Refund For Purchase Of Land For Industries', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/Stamp_Duty_Scheme.PDF\" target=\"_blank\">STAMP DUTY REFUND SCHEME - DOWNLOAD</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:15px; width:20px\">\r\n			<p><strong>1</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:135px\">\r\n			<p><strong>Name of the scheme</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:470px\">\r\n			<p><strong>Stamp Duty Refund</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:231px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:231px; width:135px\">\r\n			<p>Important Instructions</p>\r\n			</td>\r\n			<td style=\"height:231px; width:470px\">\r\n			<ol>\r\n				<li>All the new Micro, Small, Medium, Large, Mega Units &amp; Industrial Estate Developers (who have purchased/taken the land after 14.08.2015 and before 14.08.2020 shall be admissible for the refund of stamp duty). Such units should have gone into commercial production after the notification of Enterprises Promotion Policy 2015 i.e. 14th August, 2015, are eligible.</li>\r\n				<li>The unit should not have been placed in the restrictive list as notified by the State Government from time to time.</li>\r\n				<li>The unit should have obtained NOC/CLU from competent Authority, if applicable.</li>\r\n				<li>The unit should be in commercial production.</li>\r\n				<li>All the general category industrial units are also eligible Stamp Duty Refund applicable to Micro, Small, Medium &amp; Large category units, even if they are not covered under the thrust areas.</li>\r\n				<li>The developer of Private Industrial Estate/ Industrial parks are eligible for refund of stamp duty after development of complete infrastructure in the industrial estate/ parks and obtained license from Competent Authority, if applicable.</li>\r\n				<li>In case of rejection of case, an appeal shall lie with the Director of Industries /Administrative Secretary, Industries Haryana within a period of 30 days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:131px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:131px; width:135px\">\r\n			<p>Documents to be enclosed with the request</p>\r\n			</td>\r\n			<td style=\"height:131px; width:470px\">\r\n			<ol>\r\n				<li>Application form on Annexure-I</li>\r\n				<li>EM/UAM/IEM-Part II, if applicable.</li>\r\n				<li>Photocopies of the Sale deed/lease deed registered with concerned sub-registrar showing the details of stamp duty.</li>\r\n				<li>Mutation of the above sale deed/lease deed alongwith the khasra numbers of the land.</li>\r\n				<li>Nakal Aks-shajra of the land.</li>\r\n				<li>A verification report from the concerned Tehsildar regarding registration of Sales deed(s) and payment of Stamp duty</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:165px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:165px; width:135px\">\r\n			<p>Timeline and Procedure</p>\r\n			</td>\r\n			<td style=\"height:165px; width:470px\">\r\n			<ol>\r\n				<li>Submit application online with all the required documents within 03 months after commencement of commercial production</li>\r\n				<li>Online application submitted shall be processed and examined by concerned Joint Director/ Deputy Director.</li>\r\n				<li>If any discrepancy is found, the application shall be put onhold and be communicated to the applicant within 10 working days.</li>\r\n				<li>The applicant will rectify the deficiencies within 06 weeks failing which the application will liable to be rejected.</li>\r\n				<li>After completion of the application, the approval letter will be issued within 60 working days.</li>\r\n				<li>The applicant will submit the documents mentioned in approval letter within 15 days.</li>\r\n				<li>After compliance of conditions of approval letter, the sanction letter for releasing the incentive will be issued within 15 working days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 16, '', '', NULL, b'1', 7, '2020-02-22 13:23:00', '2020-05-12 12:43:40');
