(8, 'VAT/GST Refund Scheme', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/investment%20subsidy%20on%20VAT-2015.pdf\" target=\"_blank\">VAT/GST REFUND SCHEME - DOWNLOAD</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:15px; width:20px\">\r\n			<p><strong>1</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:135px\">\r\n			<p><strong>Name of the scheme</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:470px\">\r\n			<p><strong>Investment Subsidy on VAT / SGST</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:200px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:200px; width:135px\">\r\n			<p>Important Instructions</p>\r\n			</td>\r\n			<td style=\"height:200px; width:470px\">\r\n			<ol>\r\n				<li>The Ultra, Mega and Large units should have filed IEM with Department of Industrial Policy and Promotion, Govt. of India.</li>\r\n				<li>The Micro, Small and Medium enterprise should have filed EM part-II/ Udyog Adhaar Memorandum with concerned District Industries Centre.</li>\r\n				<li>The new investment in land, building, plant &amp; machinery shall only be considered while working out eligible Fixed Capital Investment.</li>\r\n				<li>The unit should not have been placed in the restrictive list as notified by the State government from time to time.</li>\r\n				<li>NOC/CLU is required from competent Authority, if applicable.</li>\r\n				<li>The unit should be in regular production at the time of disbursement of subsidy.</li>\r\n				<li>In case of rejection of case, an appeal shall lie with the Administrative Secretary, Industries and Commerce, Haryana within a period of 30 days.</li>\r\n				<li>The other terms and conditions will be applicable as laid down in the notification of the scheme.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:29px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:29px; width:135px\">\r\n			<p>Documents to be enclosed with the request</p>\r\n			</td>\r\n			<td style=\"height:29px; width:470px\">\r\n			<ol>\r\n				<li>Copy of Certificate of Incorporation/ Partnership deed.</li>\r\n				<li>Copy of Board resolution/ Power of attorney.</li>\r\n				<li>Copy of IEM/ EM Part-II/ UAM Registration</li>\r\n				<li>CA Certificate regarding fixed capital investment in land, building, Plant &amp; machinery (Annexure-II)(existing and investment for expansion in case of existing units &amp; investment for new units separately).</li>\r\n				<li>List of new Plant &amp; machinery (Date of purchase -Name of the plant &amp; machinery &mdash;Value in Rs.(Annexure-III)</li>\r\n				<li>Copies of the purchase invoices of plant &amp; machinery/ equipments in sequence with details mentioned under Annexure-III.</li>\r\n				<li>Audited balance sheet of the unit of last year (Applicable in case of existing Enterprises).</li>\r\n				<li>Certificate from concerned Deputy Excise &amp; Taxation Commissioner regarding net VAT/SGST deposited by the unit during the financial year for which claim has been filed(Annexure-IV)</li>\r\n				<li>Copy of first sale bill.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:156px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:156px; width:135px\">\r\n			<p>Timeline and Procedure</p>\r\n			</td>\r\n			<td style=\"height:156px; width:470px\">\r\n			<ol>\r\n				<li>Submit application online with all the required documents within 03 months of the closing of financial year for which incentive is being claimed or date of notification of the scheme, whichever is later</li>\r\n				<li>Online application submitted shall be processed and examined by concerned Joint Director/ Deputy Director.</li>\r\n				<li>If any discrepancy is found, the application shall be put onhold and be communicated to the applicant within 10 working days.</li>\r\n				<li>The applicant will rectify the deficiencies within 06 weeks failing which the application will liable to be rejected.</li>\r\n				<li>After completion of the application, the approval letter will be issued within 60 working days.</li>\r\n				<li>The applicant will submit the documents mentioned in approval letter within 15 days.</li>\r\n				<li>Further, the sanction letter for releasing the incentive will be issued within 15 working days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 17, '', '', NULL, b'1', 8, '2020-02-22 13:23:00', '2020-05-12 12:44:21'),
(9, 'Electricity Duty Exemption For Industry', '<div style=\"text-align: center;\"><a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/Electricity_Duty_Exemption_2015.PDF\" target=\"_blank\" style=\"background-color: rgb(255, 255, 255); font-size: medium;\">ELECTRICITY DUTY EXEMPTION FOR INDUSTRY - DOWNLOAD</a></div>', 18, '', '', NULL, b'1', 9, '2020-02-22 13:23:00', '2020-05-12 14:21:45'),
(10, 'Freight Assistance Scheme For Industries', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/Frieght%20Scheme%20Notification%202016-17.pdf\" target=\"_blank\">FREIGHT ASSISTANCE SCHEME - DOWNLOAD</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:15px; width:20px\">\r\n			<p><strong>1</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:135px\">\r\n			<p><strong>Name of the scheme</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:470px\">\r\n			<p><strong>Freight Assistance</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:179px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:179px; width:135px\">\r\n			<p>Important Instructions</p>\r\n			</td>\r\n			<td style=\"height:179px; width:470px\">\r\n			<ol>\r\n				<li>Large exporting units are not eligible.</li>\r\n				<li>Merchant exporters are not eligible.</li>\r\n				<li>CLU/NOC is required from the competent authority, if applicable</li>\r\n				<li>Shipping bills having state of origin of Haryana are only eligible.</li>\r\n				<li>Shipping bills pertaining to Air Export are not eligible</li>\r\n				<li>Freight Assistance admissible only on direct export.</li>\r\n				<li>Freight Assistance admissible only on the goods manufactured by the unit.</li>\r\n				<li>Shipping bills whose lading date is during FY 2016-17 shall only be included in the claim application.</li>\r\n				<li>Sequence of shipping bills and BRCs should be in accordance with the details filled in the application form.</li>\r\n				<li>In case of order of rejection of the case by the competent authority, an appeal shall lie with Director of Industries</li>\r\n			</ol>\r\n\r\n			<p>/Principal Secretary, Industries within a period of 30 days.</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:239px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:239px; width:135px\">\r\n			<p>Documents to be enclosed with the request</p>\r\n			</td>\r\n			<td style=\"height:239px; width:470px\">\r\n			<ol>\r\n				<li>Acknowledgement of EM Part-II/ UAM</li>\r\n				<li>Certificate in Form &ldquo;A&rdquo; and Form &ldquo;C&rdquo; issued by Registrar/District Registrar of Firms &amp; societies, alongwith a copy of</li>\r\n			</ol>\r\n\r\n			<p>Partnership Deed/Certificate of the Incorporation of the Company in case of limited companies.</p>\r\n\r\n			<ol>\r\n				<li>Special Power of Attorney (duly attested by Notary Public)/ Resolution of Board of Directors of the Company in Form (b)/F(c) in original in case of limited companies</li>\r\n				<li>Registration-cum-membership issued by relevant Export Promotion Council.</li>\r\n				<li>Certificate of Importer Exporter Code (IEC) issued by DGFT</li>\r\n				<li>Shipping Bills(EP Copy).</li>\r\n				<li>Bank Realization Certificate of concerned shipping bill.</li>\r\n				<li>Land proof-Sale deed/allotment letter/rent deed/lease deed</li>\r\n				<li>Change of Land use (CLU)/NOC from competent authority, if applicable.</li>\r\n				<li>Chartered Accountant Certificate in respect of total FOB Value in the prescribed formF(d) and total eligible amount of Freight paid from place of manufacture to sea port in the prescribed formF (e)</li>\r\n				<li>Chartered Accountant Certificate in respect of investment in Plant and Machinery in prescribed form F(f).</li>\r\n				<li>Details of exports in Annexure-1</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:176px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:176px; width:135px\">\r\n			<p>Timeline and Procedure</p>\r\n			</td>\r\n			<td style=\"height:176px; width:470px\">\r\n			<ol>\r\n				<li>Submit application online with all the required documents within 03 months from the date of closing of the Financial Year in which the products were shipped for exports.</li>\r\n				<li>Online application submitted shall be processed and examined by concerned Joint Director/ Deputy Director</li>\r\n				<li>If any discrepancy is found, the application shall be put onhold and be communicated to the applicant within 10 working days.</li>\r\n				<li>The applicant will rectify the deficiencies within 06 weeks failing which the application will liable to be rejected.</li>\r\n				<li>After completion of the application, the approval letter will be issued within 60 working days.</li>\r\n				<li>The applicant will submit the documents mentioned in approval letter within 15 days.</li>\r\n				<li>After compliance of conditions of approval letter, the sanction letter for releasing the incentive will be issued within 15 working days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 19, '', '', NULL, b'1', 10, '2020-02-22 13:23:00', '2020-05-12 14:23:04'),
(11, 'Amendment Under Freight Assistance Scheme', '<div style=\"text-align: center;\"><a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/Amendment%20in%20Freight%20Assistance%20scheme.pdf\" target=\"_blank\" style=\"background-color: rgb(255, 255, 255); font-size: large;\">FREIGHT ASSISTANCE SCHEME - DOWNLOAD</a></div>', 20, '', '', NULL, b'1', 11, '2020-02-22 13:23:00', '2020-05-12 14:23:41'),
(12, 'Tourism Sector Scheme For Industries', '<div style=\"text-align: center;\"><a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/tourism.pdf\" target=\"_blank\" style=\"background-color: rgb(255, 255, 255); font-size: large;\">TOURISM SECTOR SCHEME - DOWNLOAD</a></div>', 21, '', '', NULL, b'1', 12, '2020-02-22 13:23:00', '2020-05-12 14:24:11'),
(13, 'Design Clinic Scheme For Industries', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/Design_Clinic_Scheme_2015.pdf\" target=\"_blank\">DESIGN CLINIC SCHEME - DOWNLOAD</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:15px; width:20px\">\r\n			<p><strong>1</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:135px\">\r\n			<p><strong>Name of the scheme</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:470px\">\r\n			<p><strong>Design Clinic Scheme</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:125px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:125px; width:135px\">\r\n			<p>Important Instructions</p>\r\n			</td>\r\n			<td style=\"height:125px; width:470px\">\r\n			<ol>\r\n				<li>Any micro, small and medium unit existing anywhere in State of Haryana, is eligible as an individual or group of MSME.</li>\r\n				<li>Large units are not eligible.</li>\r\n				<li>The items of manufacture should not fall in the restrictive list as notified by State Government from time to time.</li>\r\n				<li>CLU/NOC is required from competent authority, if applicable.</li>\r\n				<li>The unit should be in regular production at the time of disbursement of assistance.</li>\r\n				<li>In case of rejection of case, an appeal shall lie with the Principal Secretary, Industries within a period of 30 days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:127px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:127px; width:135px\">\r\n			<p>Documents to be enclosed with the request</p>\r\n			</td>\r\n			<td style=\"height:127px; width:470px\">\r\n			<ol>\r\n				<li>Ack. EM part-II/UAM Registration</li>\r\n				<li>Copy of first sale bill.</li>\r\n				<li>Certificate of Incorporation/ Partnership deed.</li>\r\n				<li>Board resolution/ Power of attorney</li>\r\n				<li>Change of Land Use (CLU)/ NOC from competent authority, if applicable.</li>\r\n				<li>Copy of sanction letter of Ministry of MSME, Government of India issued under the scheme</li>\r\n				<li>Copies of payment of the contribution of the industry.</li>\r\n				<li>Audited balance sheet of the unit of last year</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:184px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:184px; width:135px\">\r\n			<p>Timeline and Procedure</p>\r\n			</td>\r\n			<td style=\"height:184px; width:470px\">\r\n			<ol>\r\n				<li>Submit application online with all the required documents within 03 months from the date of completion of awareness program and design projects</li>\r\n				<li>Online application submitted shall be processed and examined by concerned Joint Director/ Deputy Director.</li>\r\n				<li>If any discrepancy is found, the application shall be put onhold and be communicated to the applicant within 10 working days.</li>\r\n				<li>The applicant will rectify the deficiencies within 06 weeks failing which the application will liable to be rejected.</li>\r\n				<li>After completion of the application, the approval letter will be issued within 60 working days.</li>\r\n				<li>The applicant will submit the documents mentioned in approval letter within 15 days.</li>\r\n				<li>After compliance of conditions of approval letter, the sanction letter for releasing the incentive will be issued within 15 working days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 22, '', '', NULL, b'1', 13, '2020-02-22 13:23:00', '2020-05-12 14:24:45'),
(14, 'Energy Audit Scheme For Industries', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/Energy_Audit_Scheme-2015.pdf\" target=\"_blank\">ENERGY AUDIT SCHEME - DOWNLOAD</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:15px; width:20px\">\r\n			<p><strong>1</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:135px\">\r\n			<p><strong>Name of the scheme</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:470px\">\r\n			<p><strong>Energy Audit Scheme</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:160px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:160px; width:135px\">\r\n			<p>Important Instructions</p>\r\n			</td>\r\n			<td style=\"height:160px; width:470px\">\r\n			<ol>\r\n				<li>All micro, small and medium unit existing anywhere in State of Haryana shall be eligible provided, if the energy audit has been done by an Energy Auditor certified by the Bureau of Energy Efficiency (BEE).</li>\r\n				<li>Investment in new plant &amp; machinery and equipments focussed towards enhancing energy efficiency shall only be eligible.</li>\r\n				<li>Large units are not eligible.</li>\r\n				<li>The items of manufacture should not fall in the restrictive list as notified by State Government from time to time.</li>\r\n				<li>CLU/NOC is required from competent authority, if applicable.</li>\r\n				<li>The unit should be in regular production at the time of disbursement of assistance.</li>\r\n				<li>In case of rejection of case, an appeal shall lie with the Principal Secretary, Industries within a period of 30 days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:143px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:143px; width:135px\">\r\n			<p>Documents to be enclosed with the request</p>\r\n			</td>\r\n			<td style=\"height:143px; width:470px\">\r\n			<ol>\r\n				<li>Ack. EM part-II/UAM Registration.</li>\r\n				<li>Certificate of Incorporation/ Partnership deed.</li>\r\n				<li>Board resolution/ Power of attorney</li>\r\n				<li>Change of Land Use (CLU)/ NOC from competent authority, if applicable.</li>\r\n				<li>Copy of first sale bill.</li>\r\n				<li>Copy of Energy audit certification.</li>\r\n				<li>CA certificate of Expenditure (in original) giving the details ( as per prescribed format)</li>\r\n				<li>Copy of the bills raised by certifying agency and proof of payment thereof.</li>\r\n				<li>Audited balance sheet of the unit of last year</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:177px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:177px; width:135px\">\r\n			<p>Timeline and Procedure</p>\r\n			</td>\r\n			<td style=\"height:177px; width:470px\">\r\n			<ol>\r\n				<li>Submit application online with all the required documents within 03 months from the date of completion of energy audit/purchase of capital equipments.</li>\r\n				<li>Online application submitted shall be processed and examined by concerned Joint Director/ Deputy Director.</li>\r\n				<li>If any discrepancy is found, the application shall be put onhold and be communicated to the applicant within 10 working days.</li>\r\n				<li>The applicant will rectify the deficiencies within 06 weeks failing which the application will liable to be rejected.</li>\r\n				<li>After completion of the application, the approval letter will be issued within 60 working days.</li>\r\n				<li>The applicant will submit the documents mentioned in approval letter within 15 days.</li>\r\n				<li>After compliance of conditions of approval letter, the sanction letter for releasing the incentive will be issued within 15 working days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 23, '', '', NULL, b'1', 14, '2020-02-22 13:23:00', '2020-05-12 14:25:17'),
