(27, 'Notification of Quality Certification Scheme under EPP 2015', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/Quality_Certification_Scheme.PDF\" target=\"_blank\">Notification of Quality Certification Scheme under EPP 2015 - DOWNLOAD</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:15px; width:20px\">\r\n			<p><strong>1</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:135px\">\r\n			<p><strong>Name of the scheme</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:470px\">\r\n			<p><strong>Quality Certification Assistance Scheme</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:109px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:109px; width:135px\">\r\n			<p>Important Instructions</p>\r\n			</td>\r\n			<td style=\"height:109px; width:470px\">\r\n			<ol>\r\n				<li>All micro, small and medium enterprises existing anywhere in State of Haryana, shall be eligible.</li>\r\n				<li>Large industrial units are not eligible.</li>\r\n				<li>The items of manufacture should not fall in the restrictive list as notified by State Government from time to time.</li>\r\n				<li>CLU/NOC is required from competent authority, if applicable.</li>\r\n				<li>The unit should be in regular production at the time of disbursement of assistance.</li>\r\n				<li>In case of rejection of case, an appeal shall lie with the Director of Industries within a period of 30 days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:127px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:127px; width:135px\">\r\n			<p>Documents to be enclosed with the request</p>\r\n			</td>\r\n			<td style=\"height:127px; width:470px\">\r\n			<ol>\r\n				<li>Acknowledgement of EM Part-II/UAM registration.</li>\r\n				<li>Certificate of Incorporation/ Partnership deed.</li>\r\n				<li>Board resolution/ Power of attorney.</li>\r\n				<li>ISO/ HACCP/ BSI/ WHO-GMP/ZED/TS/Hallmark Certification.</li>\r\n				<li>Change of Land Use (CLU)/ NOC from competent authority, if applicable.</li>\r\n				<li>Undertaking/Declaration on non-judicial stamp paper(Annexure-II)</li>\r\n				<li>CA certificate of details of Expenditure incurred (in original) (Annexure-III)</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:183px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:183px; width:135px\">\r\n			<p>Timeline and Procedure</p>\r\n			</td>\r\n			<td style=\"height:183px; width:470px\">\r\n			<ol>\r\n				<li>Submit application online with all the required documents within 03 months of date of acquiring of ISO/ HACCP/ BSI/ WHO- GMP/ ZED/TS/Hallmark certification</li>\r\n				<li>Online application submitted shall be processed and examined by concerned Joint Director/ Deputy Director.</li>\r\n				<li>If any discrepancy is found, the application shall be put onhold and be communicated to the applicant within 10 working days.</li>\r\n				<li>The applicant will rectify the deficiencies within 06 weeks failing which the application will liable to be rejected.</li>\r\n				<li>After completion of the application, the approval letter will be issued within 60 working days.</li>\r\n				<li>The applicant will submit the documents mentioned in approval letter within 15 days.</li>\r\n				<li>After compliance of conditions of approval letter, the sanction letter for releasing the incentive will be issued within 15 working days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 29, '', '', NULL, b'1', 27, '2020-02-22 13:23:00', '2020-02-22 14:02:33'),
(28, 'Notification of Safety Audit Scheme under EPP 2015', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/Safety_Audit_Scheme-2015.PDF\" target=\"_blank\">Notification of Safety Audit Scheme under EPP 2015 - DOWNLOAD</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:15px; width:20px\">\r\n			<p><strong>1</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:135px\">\r\n			<p><strong>Name of the scheme</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:470px\">\r\n			<p><strong>Safety Audit Scheme</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:127px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:127px; width:135px\">\r\n			<p>Important Instructions</p>\r\n			</td>\r\n			<td style=\"height:127px; width:470px\">\r\n			<ol>\r\n				<li>Only micro, small and medium enterprises (new &amp; existing) anywhere in State of Haryana, shall be eligible provided the safety audit has been done by the Auditors of ISO certified agency.</li>\r\n				<li>Large industrial units are not eligible.</li>\r\n				<li>The items of manufacture should not fall in the restrictive list as notified by State Government from time to time.</li>\r\n				<li>CLU/NOC is required from competent authority, if applicable.</li>\r\n				<li>The unit should be in regular production at the time of disbursement of assistance.</li>\r\n				<li>In case of rejection of case, an appeal shall lie with the Director of Industries within a period of 30 days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:174px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:174px; width:135px\">\r\n			<p>Documents to be enclosed with the request</p>\r\n			</td>\r\n			<td style=\"height:174px; width:470px\">\r\n			<ol>\r\n				<li>Acknowledgement of EM Part-II/UAM registration</li>\r\n				<li>Certificate of Incorporation/ Partnership deed.</li>\r\n				<li>Board resolution/ Power of attorney</li>\r\n				<li>Change of Land Use (CLU)/ NOC from competent authority, if applicable.</li>\r\n				<li>Copy of first sale bill.</li>\r\n				<li>Copy of Safety audit certification.</li>\r\n				<li>CA certificate of Expenditure (in original) giving the details (Annexure-III)</li>\r\n				<li>Copy of the bills raised by certifying agency and proof of payment thereof.</li>\r\n				<li>Audited balance sheet of the unit of last year.</li>\r\n				<li>Undertaking /Declaration on non-judicial stamp paper (Annexure-II)</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:175px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:175px; width:135px\">\r\n			<p>Timeline and Procedure</p>\r\n			</td>\r\n			<td style=\"height:175px; width:470px\">\r\n			<ol>\r\n				<li>Submit application online with all the required documents within 03 months from the date of completion of safety audit/purchase of capital equipments</li>\r\n				<li>Online application submitted shall be processed and examined by concerned Joint Director/ Deputy Director.</li>\r\n				<li>If any discrepancy is found, the application shall be put onhold and be communicated to the applicant within 10 working days.</li>\r\n				<li>The applicant will rectify the deficiencies within 06 weeks failing which the application will liable to be rejected.</li>\r\n				<li>After completion of the application, the approval letter will be issued within 60 working days.</li>\r\n				<li>The applicant will submit the documents mentioned in approval letter within 15 days.</li>\r\n				<li>After compliance of conditions of approval letter, the sanction letter for releasing the incentive will be issued within 15 working days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 30, '', '', NULL, b'1', 28, '2020-02-22 13:23:00', '2020-02-22 14:02:33'),
