(33, 'Credit Linked Interest Subsidy Scheme for Technology Up-Gradation', '<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/Formatted_Final_Draft%20CLCSS_Tech%20upgradation.pdf\" target=\"_blank\">Credit Linked Interest Subsidy Scheme for Technology Up-Gradation - DOWNLOAD</a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<table border=\"1\" cellpadding=\"0\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"height:15px; width:20px\">\r\n			<p><strong>1</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:135px\">\r\n			<p><strong>Name of the scheme</strong></p>\r\n			</td>\r\n			<td style=\"height:15px; width:470px\">\r\n			<p><strong>Credit Linked Interest Subsidy Scheme</strong></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:187px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:187px; width:135px\">\r\n			<p>Important Instructions</p>\r\n			</td>\r\n			<td style=\"height:187px; width:470px\">\r\n			<ol>\r\n				<li>1. All existing micro and small units located in Haryana State covered under Credit Linked Capital Subsidy Scheme of Govt. of India for technology up-gradation, are eligible.</li>\r\n				<li>Units graduating from small scale to medium scale on account of sanction of additional loan under CLCSS are also eligible.</li>\r\n				<li>Large units are not eligible.</li>\r\n				<li>The unit must upgrade their existing plant and machinery with State of Art Technology.</li>\r\n				<li>The items of manufacture should not fall in the restrictive list as notified by State Government from time to time.</li>\r\n				<li>CLU/NOC is required from competent authority, if applicable.</li>\r\n				<li>The unit should be in regular production at the time of disbursement of subsidy as well regular in repayment of term loan installments.</li>\r\n				<li>In case of rejection of case, an appeal shall lie with the Principal Secretary, Industries within a period of 30 days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:159px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:159px; width:135px\">\r\n			<p>Documents to be enclosed with the request</p>\r\n			</td>\r\n			<td style=\"height:159px; width:470px\">\r\n			<ol>\r\n				<li>EM part-II/UAM registration.</li>\r\n				<li>Certificate of Incorporation/ Partnership deed.</li>\r\n				<li>Board resolution/ Power of attorney</li>\r\n				<li>Change of Land Use (CLU)/ NOC from competent authority, if applicable.</li>\r\n				<li>Certificate for Financial Institution on Letter Head of the Bank(Annexure-II)</li>\r\n				<li>CA Certificate regarding investment in plant &amp; machinery for new unit/expansion/ diversification(Annexure-III)</li>\r\n				<li>Audited balance sheet of the unit of last year (if Applicable).</li>\r\n				<li>Copy of first sale bill.</li>\r\n				<li>Copy of sanction/disbursal letter in respect of capital subsidy under CLCSS Scheme of GoI</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"height:179px; width:20px\">\r\n			<p>&nbsp;</p>\r\n			</td>\r\n			<td style=\"height:179px; width:135px\">\r\n			<p>Timeline and Procedure</p>\r\n			</td>\r\n			<td style=\"height:179px; width:470px\">\r\n			<ol>\r\n				<li>Submit application online with all the required documents within 03 months of the closing of the financial year for which CLIS is being claimed.</li>\r\n				<li>Online application submitted shall be processed and examined by concerned Joint Director/ Deputy Director.</li>\r\n				<li>If any discrepancy is found, the application shall be put onhold and be communicated to the applicant within 10 working days.</li>\r\n				<li>The applicant will rectify the deficiencies within 06 weeks failing which the application will liable to be rejected.</li>\r\n				<li>After completion of the application, the approval letter will be issued within 60 working days.</li>\r\n				<li>The applicant will submit the documents mentioned in approval letter within 15 days.</li>\r\n				<li>After compliance of conditions of approval letter, the sanction letter for releasing the incentive will be issued within 15 working days.</li>\r\n			</ol>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 35, '', '', NULL, b'1', 33, '2020-02-22 13:23:00', '2020-02-22 14:02:33'),
(34, 'Notification of Critical Infrastructure Development Scheme under EPP 2015', '<div style=\"text-align: center;\"><a href=\"https://haryanaindustries.gov.in/themes/backend/uploads/Uploads/Critical%20Infrastructure%20Development%20Scheme.pdf\" target=\"_blank\" style=\"background-color: rgb(255, 255, 255);\">Notification of Critical Infrastructure Development Scheme under EPP 2015 - DOWNLOAD</a></div>', 36, '', '', NULL, b'1', 34, '2020-02-22 13:23:00', '2020-02-22 14:02:33'),
(35, 'Mega Projects', '<p align=\"center\"><span style=\"color: #ff0000; font-size: small;\"><strong>Incentives and Infrastructure</strong></span></p> <p align=\"center\"><span style=\"color: #ff0000; font-size: small;\"><strong>Available as per</strong></span></p> <p align=\"center\"><span style=\"color: #ff0000; font-size: small;\"><strong>Enterprises Promotion Policy-2015</strong></span></p> <p align=\"center\"><span style=\"color: #ff0000; font-size: small;\"><strong>&nbsp;</strong></span></p> <p><span style=\"color: #ff0000; font-size: small;\"><strong>MEGA PROJECTS:ULTRA</strong></span></p> <p>&nbsp;</p> <p style=\"text-align: justify;\">Ultra Mega Projects shall be offered special package of incentives by Haryana Enterprise Promotion Board including such as mixed land use, allotment of land at a price to be decided by the Government etc.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>MEGA PROJECTS (standard package of incentive):</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>&nbsp;</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>Category &lsquo;B&rsquo; Block</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>&nbsp;</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>Investment Subsidy on VAT:</strong></span></p> <p style=\"text-align: justify;\">30% of VAT/SGST net paid for first 5 years, 15% for next three years from the date of commencement of commercial production with cap of 100% of fixed capital investment.</p> <p>&nbsp;</p> <p style=\"text-align: justify;\"><span style=\"color: #ff0000;\"><strong>Employment Generation Subsidy:</strong></span></p> <p style=\"text-align: justify;\">For capacity building of persons belonging to Haryana (skilled/semi-skilled) engaged minimum of 50% of total employment, the subsidy @Rs.36,000/- per year for SC/Women and Rs.30,000/- per year for General category for 5 years or 20% of the VAT/SGST deposited whichever is less. The evidence for employment would be related statutory returns such as returns filed under the Employees Provident Fund Act, etc.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Electricity Duty Exemption:</strong></span></p> <p style=\"text-align: justify;\">100% Exemption from payment of Electricity duty for new mega project for a period of 5 years from the date of release of electricity connection.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Stamp Duty Refund:</strong></span></p> <p style=\"text-align: justify;\">50% refund of stamp duty on the land area meant for industrial use after commencement of commercial production, within 5 years from the date of filing of IEM.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>EDC Charges:</strong></span></p> <p>% exemption towards External Development Charges is exempted.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Category &lsquo;C&rsquo; Block</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>&nbsp;</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>Investment Subsidy on VAT:</strong></span></p> <p style=\"text-align: justify;\">% of VAT/SGST net paid for first 5 years, 25% for next three years from the date of commencement of commercial production with cap of 100% of fixed capital investment.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Employment Generation Subsidy:</strong></span></p> <p style=\"text-align: justify;\">For capacity building of persons belonging to Haryana (skilled/semi-skilled) engaged minimum of 50% of total employment, the subsidy @Rs.36,000/- per year for SC/Women and Rs.30,000/- per year for General category for 5 years or 20% of the VAT/SGST deposited whichever is less. The evidence for employment would be related statutory returns such as returns filed under the Employees Provident Fund Act, etc.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Electricity Duty Exemption:</strong></span></p> <p style=\"text-align: justify;\">100% Exemption from payment of Electricity duty for new mega project for a period of 5 years from the date of release of electricity connection.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Stamp Duty Refund:</strong></span></p> <p style=\"text-align: justify;\">75% refund of stamp duty on the land area meant for industrial use after commencement of commercial production, within 5 years from the date of filing of IEM.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>EDC Charges:</strong></span></p> <p>50% exemption towards External Development Charges.</p> <p>&nbsp;&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Category &lsquo;D&rsquo; Block</strong></span></p> <p><span style=\"color: #ff0000;\"><strong><br /></strong></span></p> <p><span style=\"color: #ff0000;\"><strong>Investment Subsidy on VAT:</strong></span></p> <p style=\"text-align: justify;\">75% of VAT/SGST net paid for first 5 years, 35% for next three years in &lsquo;D&rsquo; category blocks from the date of commencement of commercial production with cap of 100% of fixed capital investment.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Employment Generation Subsidy:</strong></span></p> <p style=\"text-align: justify;\">For capacity building of persons belonging to Haryana (skilled/semi-skilled) engaged minimum of 50% of total employment, the subsidy @Rs.36,000/- per year for SC/Women and Rs.30,000/- per year for General category for 5 years or 20% of the VAT/SGST deposited whichever is less. The evidence for employment would be related statutory returns such as returns filed under the Employees Provident Fund Act, etc.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Electricity Duty Exemption:</strong></span></p> <p style=\"text-align: justify;\">100% Exemption from payment of Electricity duty for new mega project for a period of 7 years from the date of release of electricity connection.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Stamp Duty Refund:</strong></span></p> <p style=\"text-align: justify;\">100% refund of stamp duty on the land area meant for industrial use after commencement of commercial production, within 5 years from the date of filing of IEM.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>EDC Charges:</strong></span></p> <p>50% exemption towards External Development Charges.</p> <p>&nbsp;</p> <p style=\"text-align: justify;\">Note: Special package of incentives over and above of the standard package for Mega Projects having potential to develop ancillary enterprises will also be considered by Haryana Enterprise Promotion Board.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>LARGE UNITS:</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>&nbsp;</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>Category &lsquo;B&rsquo; Block</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>&nbsp;</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>EDC Charges:</strong></span></p> <p>50% exemption towards External Development Charges.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Category &lsquo;C&rsquo; Block</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>&nbsp;</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>Investment Subsidy on VAT:</strong></span></p> <p style=\"text-align: justify;\">75% of VAT/SGST net paid for first 5 years, 25% for next three years from the date of commencement of commercial production with cap of 100% of fixed capital investment.</p> <p style=\"text-align: justify;\">50% of VAT/SGST net paid for 5 years on 50% additional investment in plant and machinery for expansion/diversification with cap of 100% of fixed capital investment on expanded FCI.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Employment Generation Subsidy:</strong></span></p> <p style=\"text-align: justify;\">For capacity building of persons belonging to Haryana (skilled/semi-skilled) engaged minimum of 50% of total employment, the subsidy @Rs.36,000/- per year for SC/Women and Rs.30,000/- per year for General category for 5 years or 20% of the VAT/SGST deposited whichever is less.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Electricity Duty Exemption:</strong></span></p> <p>100% Exemption only for new enterprise for 5 years from the date of release of electricity connection.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Stamp Duty:</strong></span></p> <p style=\"text-align: justify;\">75% refund of stamp duty on the land area meant for industrial use after commencement of commercial production, within 5 years from the date of filing of IEM.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>EDC Charges:</strong></span></p> <p>50% exemption towards External Development Charges.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Category &lsquo;D&rsquo; Block</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>&nbsp;</strong></span></p> <p><span style=\"color: #ff0000;\"><strong>Investment Subsidy on VAT:</strong></span></p> <p style=\"text-align: justify;\">75% of VAT/SGST net paid for first 7 years, 35% for next three years from the date of commencement of commercial production with cap of 100% of fixed capital investment.</p> <p style=\"text-align: justify;\">75% of VAT/SGST net paid for 7 years on 50% additional investment in plant and machinery for expansion/diversification with cap of 100% of fixed capital investment on expanded FCI.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Employment Generation Subsidy:</strong></span></p> <p style=\"text-align: justify;\">For capacity building of persons belonging to Haryana (skilled/semi-skilled) engaged minimum of 50% of total employment, the subsidy @Rs.36,000/- per year for SC/Women and Rs.30,000/- per year for General category for 5 years or 20% of the VAT/SGST deposited whichever is less.</p> <p>&nbsp;</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Electricity Duty Exemption:</strong></span></p> <p>100% Exemption only for new enterprise for 7 years from the date of release of electricity connection.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>Stamp Duty:</strong></span></p> <p style=\"text-align: justify;\">100% refund of stamp duty on the land area meant for industrial use after commencement of commercial production, within 5 years from the date of filing of IEM.</p> <p>&nbsp;</p> <p><span style=\"color: #ff0000;\"><strong>EDC Charges:</strong></span></p> <p>50% exemption towards External Development Charges.</p> <p>&nbsp;</p>', 8, '', '', NULL, b'1', 35, '2020-02-22 13:23:00', '2020-02-22 14:02:33');
